import React,{useState} from "react";
import "./style.css";
import Menu_AdminPage from "./../../../components/menu_adminpage";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faEdit } from "@fortawesome/free-solid-svg-icons";
import { faSave, faMoneyBillAlt } from "@fortawesome/free-regular-svg-icons";
import {
  Select,
  Popconfirm,
  message,
  Modal,
  Form,
  Input,
  DatePicker,
  Space,
  Radio,
  Upload,
  Button,
  Table,
  InputNumber,
} from "antd";
function Expenditure() {
  //DatePicker
  function onChange(date, dateString) {
    console.log(date, dateString);
  }
  //modal
  const [isModalVisible, setIsModalVisible] = useState(false);
  const showModal = () => {
    setIsModalVisible(true);
  };
  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };
  const [isModalVisible_1, setIsModalVisible_1] = useState(false);
  const showModal_1 = () => {
    setIsModalVisible_1(true);
  };
  const handleOk_1 = () => {
    setIsModalVisible_1(false);
  };

  const handleCancel_1 = () => {
    setIsModalVisible_1(false);
  };
  const [isModalVisible_2, setIsModalVisible_2] = useState(false);
  const showModal_2 = () => {
    setIsModalVisible_2(true);
  };
  const handleOk_2 = () => {
    setIsModalVisible_2(false);
  };

  const handleCancel_2 = () => {
    setIsModalVisible_2(false);
  };
  //Rangepicker
  const { RangePicker } = DatePicker;
  //inpurtnumber
  function onChange_inputnum(value) {
    console.log("changed", value);
  }
  //Popconfirm
  function confirm(e) {
    console.log(e);
    message.success("Click on Yes");
  }

  function cancel(e) {
    console.log(e);
    message.error("Click on No");
  }
  //table
  const columns = [
    {
      title: "Phòng",
      dataIndex: "room",
      key: "room",
    },
    {
      title: "Tên khoản chi",
      dataIndex: "name",
      key: "name",
    },
    {
      title: "Ngày chi",
      dataIndex: "date",
      key: "date",
    },
    {
      title: "Loại khoản chi",
      dataIndex: "type",
      key: "type",
    },
    {
      title: "Số tiền chi",
      dataIndex: "money",
      key: "money",
    },
    {
      title: "Mô tả",
      dataIndex: "description",
      key: "description",
    },
    {
      title: "",
      dataIndex: "",
