import React, { useState } from "react";
import "./style.css";
import { Images } from "./../../../config/image";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faTachometerAlt,
  faSitemap,
  faPlu,
  faPlus
} from "@fortawesome/free-solid-svg-icons";
import {faSave} from '@fortawesome/free-regular-svg-icons'
import Menu_AdminPage from "./../../../components/menu_adminpage";
import { Table, Popconfirm, message, Modal, Form, Input } from "antd";
import arr_data_brand from "./../../../mock/data_brand";
function Brand(props) {
  function confirm(e) {
    console.log(e);
    message.success("Click on Yes");
  }

  function cancel(e) {
    console.log(e);
    message.error("Click on No");
  }
  const data = [
    {
      key: "1",
      Name: "Chi Nhánh 1",
      Floor: "2",
      Address: "2 Võ Văn Ngân",
      Name_Host: "Vũ Văn Thái",
      Description: "Sạch sẽ",
    },
    {
      key: "2",
      Name: "Chi Nhánh 1",
      Floor: "2",
      Address: "2 Võ Văn Ngân",
      Name_Host: "Vũ Văn Thái",
      Description: "Sạch sẽ",
    },
    {
      key: "3",
      Name: "Chi Nhánh 1",
      Floor: "2",
      Address: "2 Võ Văn Ngân",
      Name_Host: "Vũ Văn Thái",
      Description: "Sạch sẽ",
    },
  ];
  const columns = [
    {
      title: "Tên Chi Nhánh",
      dataIndex: "name",
      key: "Name",
    },
    {
      title: "Số Lầu",
      dataIndex: "floor",
      key: "Floor",
    },
    {
      title: "Địa Chỉ",
      dataIndex: "address",
      key: "Address",
    },
    {
      title: "Tên Chủ Nhà",
      dataIndex: "name_host",
      key: "Name_Host",
    },
    {
      title: "Mô Tả",
      dataIndex: "description",
      key: "Description",
    },
  ];
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };
  const onFinish = (values) => {
    console.log('Success:', values);
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };
  return (
    <div>
      <div
        style={{
          width: "100vmax",
          height: "100vmax",
          backgroundColor: "#efefef",
        }}
      >
        <div style={{ height: "100px" }}>
          <Menu_AdminPage />
        </div>
        <div className="rectangle">
          <div style={{ display: "block", width: "100%" }}>
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                width: "100%",
                height: "auto",
                paddingTop: "10px",
              }}
            >
              <div className="topic-left">
                <FontAwesomeIcon icon={faSitemap} size="2x" color="#007c7e" />
                <div className="content">QUẢN LÝ DANH SÁCH NHÀ TRỌ</div>
              </div>
              <div className="btn-right">
                <button className="detailed-btn" onClick={showModal}>
                  THÊM MỚI
                </button>
                <Modal
                  title={
                    <div style={{ display: "flex" }}>
                      <FontAwesomeIcon
                        icon={faPlus}
                        size="1x"
                        color="#007c7e"
                      />{" "}
                      <div
                        style={{
                          fontFamily: "PT Sans, sans-serif",
                          fontSize: "20px",
                          color: "#007c7e",
                          paddingLeft: "10px",
                          fontWeight: "bold",
                        }}
                      >
                        Thêm mới
                      </div>
                    </div>
                  }
                  visible={isModalVisible}
                  okText={<div style={{display:"flex"}}>
                    <FontAwesomeIcon icon={faSave} size ="1x" color="#007c7e" style={{marginRight:"5px"}}/>
                    <div style={{paddingRight:"10px",lineHeight:"1px"}}>LƯU LẠI</div>
                  </div>}
                  cancelText="Không"
                  okType={{color:"#007c7e"}}
                >
                 <Form>
                   <Form.Item label="Tên Tòa Nhà" name="Name">
                     <Input/>
                   </Form.Item>
                   <Form.Item label="Số Lầu" name="Floor">
                     <Input/>
                   </Form.Item>
                   <Form.Item  label="Số Phòng" name="Room">
                     <Input/>
                   </Form.Item>
                   <Form.Item  label="Địa Chỉ" name="Address">
                     <Input/>
                   </Form.Item>
                   <Form.Item  label="Ghi Chú" name="Notification">
                     <Input/>
                   </Form.Item>
                 </Form>
                </Modal>
                <Popconfirm
                  title="BẠN CÓ CHẮC MUỐN XÓA DỮ LIỆU KHÔNG?"
                  onConfirm={confirm}
                  onCancel={cancel}
                  okText="Có"
                  cancelText="Không"
                >
                  <button className="detailed-btn">XÓA NHIỀU</button>
                </Popconfirm>
              </div>
            </div>

            <div style={{ paddingTop: "30px", width: "100%", height: "auto" }}>
              <Table columns={columns} bordered dataSource={data} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Brand;
