import React from "react";
import { Input, Radio, Select } from "antd";
import "./style.css";
function register() {
  const [value, setValue] = React.useState(1);

  const onChange = (e) => {
    console.log("radio checked", e.target.value);
    setValue(e.target.value);
  };
  const { Option } = Select;

  function handleChange(value) {
    console.log(`selected ${value}`);
  }
  return (
    <div>
      <div className="form-register">
        <div className="form-box">
          <div style={{ width: "100%", display: "block" }}>
            <div className="sign-up">ĐĂNG KÝ</div>
            <div className="username">Họ và tên</div>
            <div
              style={{
                width: "90%",
                paddingLeft: "20px",
                paddingTop: "5px",
                paddingBottom: "10px",
              }}
            >
              <Input placeholder="Nhập họ và tên" />
            </div>
            <div className="username">Giới tính</div>
            <div
              style={{
                width: "90%",
                paddingLeft: "20px",
                paddingTop: "5px",
                paddingBottom: "10px",
                textAlign: "left",
              }}
            >
              <Radio.Group onChange={onChange} value={value}>
                <Radio value={1}>Nam</Radio>
                <Radio value={2}>Nữ</Radio>
              </Radio.Group>
            </div>
            <div className="username">Số điện thoại</div>
            <div
              style={{
                width: "90%",
                paddingLeft: "20px",
                paddingTop: "5px",
                paddingBottom: "10px",
              }}
            >
              <Input placeholder="Nhập số điện thoại" />
            </div>
            <div className="username">Email</div>
            <div
              style={{
                width: "90%",
                paddingLeft: "20px",
                paddingTop: "5px",
                paddingBottom: "10px",
              }}
            >
              <Input placeholder="Nhập email" />
            </div>
            <div className="username">Tỉnh</div>
            <div
              style={{
                width: "90%",
                paddingLeft: "20px",
                paddingTop: "5px",
                paddingBottom: "10px",
                textAlign:"left"
              }}
            >
              <Select
                defaultValue="Chọn tỉnh thành"
                style={{ width:330 }}
                onChange={handleChange}
              >
                <Option value="TP.HCM">TP.HCM</Option>
                <Option value="Hà Nội">Hà Nội</Option>
                <Option value="Vĩnh Long">
                </Option>
                <Option value="Cà Mau">Cà Mau</Option>
              </Select>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default register;
