import React,{useState} from "react";
import "./style.css";
import Menu_AdminPage from "./../../../components/menu_adminpage";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faTachometerAlt,
  faSitemap,
  faPlu,
  faPlus,
  faTrash,
  faEdit,
  faBed,
} from "@fortawesome/free-solid-svg-icons";
import { faSave } from "@fortawesome/free-regular-svg-icons";
import { Select, Popconfirm, message, Modal,Form,Input } from "antd";

const { Option } = Select;
function Customer() {
  //Confirm
  function confirm(e) {
    console.log(e);
    message.success("Click on Yes");
  }

  function cancel(e) {
    console.log(e);
    message.error("Click on No");
  }
  //Modal-Buttom Thêm Mới
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };
  return (
    <div>
      <div
        style={{ width: "100%", height: "100vmax", backgroundColor: "#efefef" }}
      >
        <div style={{ height: "100px" }}>
          <Menu_AdminPage />
        </div>
        <div className="box-cover">
          <div style={{ display: "block", width: "100%" }}>
            <div className="horizontal">
              <div className="topic-customer">
                <FontAwesomeIcon icon={faBed} size="2x" color="#007c7e" />
                <div className="content-1">QUẢN LÝ KHÁCH THUÊ</div>
              </div>
              <div className="box-right">
                <div>
                  <Select
                    defaultValue="Lầu"
                    style={{ width: "220px", marginRight: "40px" }}
                  >
                    <Option value="Lầu 1">Lầu 1</Option>
                    <Option value="Lầu 2">Lầu 2</Option>
                    <Option value="Lầu 3">Lầu 3</Option>
                    <Option value="Lầu 4">Lầu 4</Option>
                  </Select>
                </div>
                <div>
                  <Select
                    defaultValue="Chi Nhánh"
                    style={{ width: "220px", marginRight: "20px" }}
                  >
                    <Option value="Chi Nhánh 1">Chi Nhánh 1</Option>
                    <Option value="Chi Nhánh 2">Chi Nhánh 2</Option>
                    <Option value="Chi Nhánh 3">Chi Nhánh 3</Option>
                    <Option value="Chi Nhánh 4">Chi Nhánh 4</Option>
                  </Select>
                </div>
              </div>
            </div>
            <div
              style={{ float: "right", display: "flex", paddingTop: "10px" }}
            >
              <button className="btn-top-right" onClick={showModal}>
                THÊM MỚI
              </button>
              <Modal 
              title="Thêm mới" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}
              >
                <Form>
                    <Form.Item label="Chi Nhánh" name="Brand">
                    <Select
                    defaultValue="Chi Nhánh"
                    style={{ width: "220px", marginRight: "20px" }}
                  >
                    <Option value="Chi Nhánh 1">Chi Nhánh 1</Option>
                    <Option value="Chi Nhánh 2">Chi Nhánh 2</Option>
                    <Option value="Chi Nhánh 3">Chi Nhánh 3</Option>
                    <Option value="Chi Nhánh 4">Chi Nhánh 4</Option>
                  </Select>
                    </Form.Item>
                    <Form.Item label="Số Lầu" name="Floor">
                      <Input />
                    </Form.Item>
                    <Form.Item label="Số Phòng" name="Room">
                      <Input />
                    </Form.Item>
                    <Form.Item label="Địa Chỉ" name="Address">
                      <Input />
                    </Form.Item>
                    <Form.Item label="Ghi Chú" name="Notification">
                      <Input />
                    </Form.Item>
                  </Form>
              </Modal>
              <Popconfirm
                title="Bạn có chắc muốn xóa dữ liệu này không?"
                onConfirm={confirm}
                onCancel={cancel}
                okText="Có"
                cancelText="Không"
              >
                {" "}
                <button className="btn-top-right">XÓA NHIỀU</button>
              </Popconfirm>
              <button className="btn-top-right">TÌM KIẾM</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Customer;
