import React from "react";
import "./style.css";
import { Images } from "./../../config/image";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTachometerAlt } from "@fortawesome/free-solid-svg-icons";
import { Menu,Grid } from "antd";
const { useBreakpoint } = Grid;
const SubMenu = Menu.SubMenu;
const MenuItemGroup = Menu.ItemGroup;
function Menu_AdminPage() {
    const { md } = useBreakpoint();

  return (
    <div>
      <div
        style={{ backgroundColor: "#efefef", width: "100%", height: "100vmax" }}
      >
        <div className="menuBar_Admin">
          <div className="logo-Admin">
            <img src={Images.LOGIN} style={{ width: "60%" }} />
          </div>
          <div className="menu-child">
            <div className="left-menu-child">
              {/* <div className="com-proper">
                <div style={{ paddingTop: "2px", paddingLeft: "10px" }}>
                  <FontAwesomeIcon
                    icon={faTachometerAlt}
                    color="white"
                    size="1x"
                  />
                </div>
                <div style={{ paddingLeft: "15px" }}>Bảng Quản Trị</div>
              </div> */}
              <Menu mode={md ? "horizontal" : "inline"}>
                <SubMenu key="sub1" title={<div>Blogs</div>} style={{backgroundColor:"#007c7e",width:"100%",marginRight:"10px"}}>
                    <Menu.Item key="setting:3" style={{color:"#007c7e"}}>Option 3</Menu.Item>
                    <Menu.Item key="setting:4" style={{color:"#007c7e"}}>Option 4</Menu.Item>
                </SubMenu>
              </Menu>
            </div>
            <div className="right-menu-child"></div>
          </div>
          <button className="barMenu-admin">
            <div className="barbtn-admin"></div>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Menu_AdminPage;
