import React from "react";
import "./style.css";
import Menu_AdminPage from "./../../../components/menu_adminpage";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faTachometerAlt,
  faSitemap,
  faPlu,
  faPlus,
  faTrash,
  faEdit,
  faBed,
} from "@fortawesome/free-solid-svg-icons";
import { faSave } from "@fortawesome/free-regular-svg-icons";
import { Select } from "antd";

const { Option } = Select;
function Customer() {
  return (
    <div>
      <div
        style={{ width: "100%", height: "100vmax", backgroundColor: "#efefef" }}
      >
        <div style={{ height: "100px" }}>
          <Menu_AdminPage />
        </div>
        <div className="box-cover">
          <div style={{ display: "block", width: "100%" }}>
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                width: "100%",
                height: "auto",
                paddingTop: "10px",
              }}
            >
              <div className="topic-customer">
                <FontAwesomeIcon icon={faBed} size="2x" color="#007c7e" />
                <div className="content-1">QUẢN LÝ KHÁCH THUÊ</div>
              </div>
              <div className="box-right">
                <div>
                  <Select defaultValue="Lầu" style={{width:"220px",marginRight:"40px"}}>
                    <Option value="Lầu 1">Lầu 1</Option>
                    <Option value="Lầu 2">Lầu 2</Option>
                    <Option value="Lầu 3">Lầu 3</Option>
                    <Option value="Lầu 4">Lầu 4</Option>
                  </Select>
                </div>
                <div>
                  <Select defaultValue="Lầu" style={{width:"220px",marginRight:"30px"}}>
                    <Option value="Lầu 1">Lầu 1</Option>
                    <Option value="Lầu 2">Lầu 2</Option>
                    <Option value="Lầu 3">Lầu 3</Option>
                    <Option value="Lầu 4">Lầu 4</Option>
                  </Select>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Customer;
