import React from "react";
import "./style.css";
import { Images } from "./../../../config/image";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faTachometerAlt,
  faSitemap,
  faPlu,
} from "@fortawesome/free-solid-svg-icons";
import Menu_AdminPage from "./../../../components/menu_adminpage";
import { Table, Popconfirm,message } from "antd";
import arr_data_brand from "./../../../mock/data_brand";
function Brand(props) {
  function confirm(e) {
    console.log(e);
    message.success("Click on Yes");
  }

  function cancel(e) {
    console.log(e);
    message.error("Click on No");
  }
  const data = [
    {
      key: "1",
      Name: "Chi Nhánh 1",
      Floor: "2",
      Address: "2 Võ Văn Ngân",
      Name_Host: "Vũ Văn Thái",
      Description: "Sạch sẽ",
    },
    {
      key: "2",
      Name: "Chi Nhánh 1",
      Floor: "2",
      Address: "2 Võ Văn Ngân",
      Name_Host: "Vũ Văn Thái",
      Description: "Sạch sẽ",
    },
    {
      key: "3",
      Name: "Chi Nhánh 1",
      Floor: "2",
      Address: "2 Võ Văn Ngân",
      Name_Host: "Vũ Văn Thái",
      Description: "Sạch sẽ",
    },
  ];
  const columns = [
    {
      title: "Tên Chi Nhánh",
      dataIndex: "name",
      key: "Name",
    },
    {
      title: "Số Lầu",
      dataIndex: "floor",
      key: "Floor",
    },
    {
      title: "Địa Chỉ",
      dataIndex: "address",
      key: "Address",
    },
    {
      title: "Tên Chủ Nhà",
      dataIndex: "name_host",
      key: "Name_Host",
    },
    {
      title: "Mô Tả",
      dataIndex: "description",
      key: "Description",
    },
  ];

  return (
    <div>
      <div
        style={{
          width: "100vmax",
          height: "100vmax",
          backgroundColor: "#efefef",
        }}
      >
        <div style={{ height: "100px" }}>
          <Menu_AdminPage />
        </div>
        <div className="rectangle">
          <div style={{ display: "block", width: "100%" }}>
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                width: "100%",
                height: "auto",
              }}
            >
              <div className="topic-left">
                <FontAwesomeIcon icon={faSitemap} size="2x" color="#007c7e" />
                <div className="content">QUẢN LÝ DANH SÁCH NHÀ TRỌ</div>
              </div>
              <div className="btn-right">
                <button className="detailed-btn">THÊM MỚI</button>
                
                  
                    <button className="detailed-btn">
                  XÓA NHIỀU

                </button>
                <Popconfirm
                    title="BẠN CÓ CHẮC MUỐN XÓA DỮ LIỆU KHÔNG?"
                    onConfirm={confirm}
                    onCancel={cancel}
                    okText="Có"
                    cancelText="Không"
                  >
                  </Popconfirm>
              </div>
            </div>

            <div
              style={{ marginBottom: "30px", width: "100%", height: "auto" }}
            >
              <Table columns={columns} bordered dataSource={data} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Brand;
