import React from "react";
import "./style.css";
import { Images } from "../../config/image";
import { Input,Button } from "antd";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSignInAlt } from '@fortawesome/free-solid-svg-icons'
function login() {
  return (
    <div>
      <div className="form-login">
        <div className="form-box">
          <div style={{ width: "100%", display: "block" }}>
            <div className="sign-in">ĐĂNG NHẬP</div>
            <div className="username">Tên đăng nhập</div>
            <div
              style={{ width: "90%", paddingLeft: "20px", paddingTop: "10px" }}
            >
              <Input placeholder="Nhập email" />
            </div>
            <div className="username">Mật khẩu</div>
            <div
              style={{ width: "90%", paddingLeft: "20px", paddingTop: "10px" }}
            >
              <Input placeholder="Nhập mật khẩu" />
            </div>
            <div className="forgetPW">Quên mật khẩu</div>
            <div style={{width:"100%",height:"auto",backgroundColor:"red",display:"flex",justifyContent:"center",paddingTop:"10px",paddingRight:"5px"}}>
                <Button 
                   style={{width:"335px",height:"auto",fontSize:"20px"}}
                >
                ĐĂNG NHẬP</Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default login;
