import React from "react";
import "./style.css";
import { Images } from "./../../../config/image";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faTachometerAlt,
  faSitemap,
  faPlu,
} from "@fortawesome/free-solid-svg-icons";
import Menu_AdminPage from "./../../../components/menu_adminpage";
import { Table } from "antd";
import arr_data_brand from "./../../../mock/data_brand";
function Brand(props) {
  const columns = [
    {
      title: "Tên Chi Nhánh",
      dataIndex: "name",
      key: "name",
      render: (name) => {
        props.Name;
      },
    },
    {
      title: "Số Lầu",
      dataIndex: "floor",
      key: "floor",
      render: (floor) => {
        props.Floor;
      },
    },
    {
      title: "Địa Chỉ",
      dataIndex: "address",
      key: "address",
      render: (address) => {
        props.Address;
      },
    },
    {
      title: "Tên Chủ Nhà",
      dataIndex: "name_host",
      key: "name_Host",
      render: (name_Host) => {
        props.Name_Host;
      },
    },
    {
      title: "Mô Tả",
      dataIndex: "description",
      key: "description",
      render: (description) => {
        props.Description;
      },
    },
  ];
  return (
    <div>
      <div
        style={{
          width: "100vmax",
          height: "100vmax",
          backgroundColor: "#efefef",
        }}
      >
        <div style={{ height: "100px" }}>
          <Menu_AdminPage />
        </div>
        <div className="rectangle">
         <div style={{display:"block"}}>
         <div className="topic-left">
            <FontAwesomeIcon icon={faSitemap} size="2x" color="#007c7e" />
            <div className="content">QUẢN LÝ DANH SÁCH NHÀ TRỌ</div>
          </div>
          <div className="btn-right">
            <button className="detailed-btn">THÊM MỚI</button>
            <button className="detailed-btn">XÓA NHIỀU</button>
          </div>
          <div style={{ paddingBottom: "30px",width:"100%" }}>
            <Table
              columns={columns}
              rowClassName={() => "editable-row"}
              bordered
              backgroundColor="white"
            />
          </div>
         </div>
        </div>
      </div>
    </div>
  );
}

export default Brand;
