import React from "react";
import "./style.css";
import { Images } from "../../config/image";
import { Input, Button } from "antd";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSignInAlt, faUserPlus } from "@fortawesome/free-solid-svg-icons";
function login() {
  return (
    <div>
      <div className="form-login">
        <div className="form-box">
          <div style={{ width: "100%", display: "block" }}>
            <div className="sign-in">ĐĂNG NHẬP</div>
            <div className="username">Tên đăng nhập</div>
            <div
              style={{ width: "90%", paddingLeft: "20px", paddingTop: "10px" }}
            >
              <Input placeholder="Nhập email" />
            </div>
            <div className="username">Mật khẩu</div>
            <div
              style={{ width: "90%", paddingLeft: "20px", paddingTop: "10px" }}
            >
              <Input placeholder="Nhập mật khẩu" />
            </div>
            <div className="forgetPW">Quên mật khẩu</div>
            <div
              style={{
                width: "100%",
                height: "auto",
                display: "flex",
                justifyContent: "center",
                paddingTop: "10px",
                paddingRight: "10px",
                paddingBottom: "20px",
              }}
            >
              <Button
                style={{
                  width: "335px",
                  height: "auto",
                  fontSize: "15px",
                  backgroundColor: "#4485bc",
                  color: "white",
                }}
              >
                ĐĂNG NHẬP
              </Button>
            </div>
            <div
              style={{
                width: "100%",
                height: "auto",
                display: "flex",
                justifyContent: "center",
                backgroundColor: "#007c7e",
                
              }}
            >
                <div style={{ fontSize: "10px", color: "white",width:"80%" }}>
                  <FontAwesomeIcon icon={faUserPlus} color="white" /> Bạn chưa
                  có tài khoản,
                </div>
                <div className="sign-up">ĐĂNG KÝ NGAY</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default login;
